import React from "react";
import Header from "../Header/Header";
import "./Hero.css";
import Heart from "../../assets/heart.png";

const Hero = () => {
  return (
    <div className="hero">
      <div className="left-h">
        <Header />
        {/* Ai-Text */}
        <div className="ai">
          <div></div>
          <span>Artificially resurrecting our lost loved ones</span>
        </div>

        {/* Hero-Text */}
        <div className="hero-text">
          <div>
            <span className="stroke-text">Breathing </span>
            <span>Life</span>
          </div>
          <div>
            <span>Into Memories</span>
          </div>
          <div>
            <span>
              ReLive can provide comfort and closure to those who have lost
              loved ones.
            </span>
          </div>
        </div>

        {/* Hero Buttons */}
        <div className="hero-buttons">
          <button className="btn">Get Started</button>
          <button className="btn">Our Team</button>
        </div>
      </div>
      <div className="right-h">
        <button className="btn">Sign In</button>
        <div className="heart">
          <img src={Heart} alt="" />
          <span>Bring Back</span>
          <span>Memories</span>
        </div>
        {/* Hero Images */}
        <img src={hero_image} alt="" />
      </div>
    </div>
  );
};

export default Hero;
